import "./App.css";
import { useState } from "react";


function App() {
  const [quotes, setquotes] = useState("");
  const getQuote = () => {
    fetch("https://api.quotable.io/random").then((response) => response.json()).then((data) =>setquotes(data));
  };
  useEffect(() => {
    return () => {
      getQuote();
    };
  }, []);
  return (
    <div className="mainContianer">
      <span className="QuotesContainer">{quotes.content}</span>
      <h4 className="AutoNameContainer">{quotes.author}</h4>
      <button onClick={getQuote()}  className="MyGetQuoteButton">Get Quotes</button>
    </div>
  );
}

export default App;
